// App.js
import React, { useState } from 'react';
import './App.css';
import Header from './shoppingFolder/components/Header';
import NavBar from './shoppingFolder/components/NavBar';
import Login from './shoppingFolder/components/Login'; // Import Login component
import Signup from './shoppingFolder/components/Signup'; // Import Signup component

function App() {
  const [currentPage, setCurrentPage] = useState(''); // No default page

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div>
      <Header onPageChange={handlePageChange} />
      <NavBar/>
      <main>
        {currentPage === 'login' && <Login />}
        {currentPage === 'signup' && <Signup />}
      </main>
    </div>
  );
}

export default App;
